using UnityEngine;

public class ResetCenterOfMass : MonoBehaviour
{
    // Existing variables...
    protected GameObject gobj6;
    protected GameObject a6;
    private float timeLimit = 5f;
    private bool instantiate_gobj = false;

    // Use coroutine instead of Update for timing logic.
    void Start()
    {
        StartCoroutine(InstantiateAndDestroyCycle());
    }

    private IEnumerator InstantiateAndDestroyCycle()
    {
        while (true) // Start an infinite cycle.
        {
            // Instantiate part
            if (!instantiate_gobj)
            {
                a6 = Instantiate(gobj6);
                instantiate_gobj = true;
                yield return new WaitForSeconds(timeLimit); // Wait for the time limit before continuing.
            }

            // Destroy part
            if (instantiate_gobj)
            {
                Destroy(a6);
                instantiate_gobj = false;
                yield return new WaitForSeconds(timeLimit); // Wait for the time limit before the next cycle.
            }
        }
    }
}
