        {
            yield return new WaitForSeconds(timeLimit);

            if (!isObjectInstantiated)
            {
                a6 = Instantiate(gobj6);
                isObjectInstantiated = true;
            }
            else
            {
                Destroy(a6);
                isObjectInstantiated = false;
