private IEnumerator InstantiateAndDestroyRoutine(GameObject prefab, float delay)
{
    while (true)
    {
        yield return new WaitForSeconds(delay);
        GameObject instance = Instantiate(prefab);
        yield return new WaitForSeconds(delay);
        Destroy(instance);
    }
}

public void StartInstantiateAndDestroy(GameObject prefab, float delay)
{
    StopInstantiateAndDestroy();
    instantiateCoroutine = StartCoroutine(InstantiateAndDestroyRoutine(prefab, delay));
}

public void StopInstantiateAndDestroy()
{
    if (instantiateCoroutine != null)
    {
        StopCoroutine(instantiateCoroutine);
    }
}

private void OnEnable()
{
    if (gobj3 != null)
        StartInstantiateAndDestroy(gobj3, timeLimit);
}

private void OnDisable()
{
    StopInstantiateAndDestroy();
}
