            a2 = Instantiate(gobj2);
            timer = 0;
            instantiate_gobj = true;
        }
        if (instantiate_gobj && timer >= timeLimit )
        {
            var obj2 = a2.AddComponent<Slice>();
            obj2.DisposeObj();
            timer = 0;
            instantiate_gobj = false;
        }
    }
}

