using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using DG.Tweening;

public class brainPart2 : MonoBehaviour
{
    private Vector3 originalPos;
    public static TextMesh text;

    private bool isThisRed = false;
    private bool inTransition = false;

    // Use the renderers list
    private List<MeshRenderer> rends = new List<MeshRenderer>();

    // Update is called once per frame
    void Update()
    {
        if (Input.GetKeyDown(KeyCode.Space) &&!inTransition)
        {
            inTransition = true;

            // Fade the text color
            text.DOColor(isThisRed? Color.black : Color.red, 0.5f);

            // Change the color of all renderers
            foreach (MeshRenderer rend in rends)
            {
                Color color = rend.material.color;
                rend.material.color = new Color(color.r, color.g, color.b, isThisRed? 1f : 0f);
            }

            // Wait for the fade to complete
            StartCoroutine(Transition());
        }
    }

    private IEnumerator Transition()
    {
        yield return new WaitForSeconds(0.5f);
        inTransition = false;
    }

    // Add a renderer to the list
    private void OnTriggerEnter(Collider other)
    {
        if (other.CompareTag("Renderer"))
        {
            rends.Add(other.GetComponent<MeshRenderer>());
        }
    }

    // Remove a renderer from the list
    private void OnTriggerExit(Collider other)
    {
        if (other.CompareTag("Renderer"))
        {
            rends.Remove(other.GetComponent<MeshRenderer>());
        }
    }
}
