using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using DG.Tweening;

public class brainPart2 : MonoBehaviour
{
    private Vector3 originalPos;
    public static TextMesh text;

    private bool isThisRed = false;
    private bool inTransition = false;

    private void Awake()
    {
        originalPos = transform.position;
    }

    private void Start()
    {
        inTransition = false;
    }

    private void OnMouseDown()
    {
        if (!inTransition)
        {
            inTransition = true;
            transform.DORotate(new Vector3(0, 0, 360), 0.5f).OnComplete(TweenColorChange).SetTarget(transform);
        }
    }

    private void OnMouseEnter()
    {
        if (!inTransition)
        {
            text.text = "Click to change color";
        }
    }

    private void OnMouseExit()
    {
        if (!inTransition)
        {
            text.text = "";
        }
    }

    private void OnMouseUp()
    {
        if (!inTransition)
        {
            inTransition = true;
            TweenColorChange();
        }
    }

    private void TweenColorChange()
    {
        if (!isThisRed)
        {
            transform.DOLocalRotate(new Vector3(0, 0, 360), 0.5f).OnComplete(TweenColorChange).SetTarget(transform);
        }
        else
        {
            transform.DOScale(new Vector3(1.2f, 1.2f, 1.2f), 0.5f).OnComplete(TweenColorChange).SetTarget(transform);
        }
    }
}

