using System;
using System.Collections.Generic;
using UnityEngine;
using System.Runtime.InteropServices;
using Boundary = UnityEngine.Experimental.XR.Boundary;

private List<Vector3> _cachedGeometryList = new List<Vector3>();

public void Update()
{
	// Add new vertices to the list
	_cachedGeometryList.Add(new Vector3(0f, 0f, 0f));
	_cachedGeometryList.Add(new Vector3(1f, 0f, 0f));
	_cachedGeometryList.Add(new Vector3(1f, 1f, 0f));
	_cachedGeometryList.Add(new Vector3(0f, 1f, 0f));

	// Loop through the list, outputting the vertices
	foreach (Vector3 vertex in _cachedGeometryList)
	{
		Debug.DrawLine(Vector3.zero, vertex, Color.red);
	}
}

