using System;
using System.Collections.Generic;
using UnityEngine;
using System.Runtime.InteropServices;
using UnityEngine.Experimental.XR;

public static class BoundaryExtensions
{
	public static List<Vector3> GetGeometry(this Boundary boundary)
	{
		var geometry = boundary.GetGeometry();

		// Add boundary geometry to a list for later use
		List<Vector3> vertices = new List<Vector3>();
		foreach (var point in geometry)
		{
			vertices.Add(point.position);
		}
		return vertices;
	}
}
