using UnityEngine;
using UnityEngine.XR.Interaction.Toolkit;

public class DestroyBuggedHeldBricks : MonoBehaviour
{
    public XRInteractionManager _interactionManager;

    public XRDirectInteractor leftHand;
    public XRDirectInteractor rightHand;

    private Transform _leftHandTransform;
    private Transform _rightHandTransform;

    void Start()
    {
        _leftHandTransform = leftHand.transform;
        _rightHandTransform = rightHand.transform;
    }

    void FixedUpdate()
    {
        foreach (XRBaseInteractable interactable in _interactionManager.GetInteractables())
        {
            GameObject go = interactable.gameObject;
            if (go.transform.parent == _leftHandTransform || go.transform.parent == _rightHandTransform)
            {
                Destroy(go);
            }
        }
    }
