﻿using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using DG.Tweening;

public class brainPart2 : MonoBehaviour
{
    private Vector3 originalPos;
    private TextMesh text;

    private bool isThisRed = false;
    private bool inTransition = false;
    private Coroutine movingRoutine;
    private bool isMoving = false;

    void Start()
    {
        originalPos = transform.position;
        text = GetComponent<TextMesh>();
    }

    void Update()
    {
        if (Input.GetKeyDown(KeyCode.Space))
        {
            if (movingRoutine != null)
                StopCoroutine(movingRoutine);

            isMoving = !isMoving;
            movingRoutine = StartCoroutine(MoveObject((isMoving ? Vector3.right : originalPos), 2.0f));
        }

        if (isThisRed && isMoving)
        {
            text.color = Color.red;
        }
        else
        {
            text.color = Color.black;
        }
    }

    IEnumerator MoveObject(Vector3 destination, float time)
    {
        float elapsedTime = 0;
        Vector3 startingPos = transform.position;
        while (elapsedTime < time)
        {
            transform.position = Vector3.Lerp(startingPos, destination, elapsedTime / time);
            elapsedTime += Time.deltaTime;
            yield return null;
        }
        transform.position = destination;
    }

    void OnTriggerStay(Collider other)
    {
        if (other.gameObject.CompareTag("RedLight"))
        {
            isThisRed = true;
        }
    }

    void OnTriggerExit(Collider other)
    {
        if (other.gameObject.CompareTag("RedLight"))
        {
            isThisRed = false;
        }
    }

    //You need to have the Tag named "RedLight" on objects that will trigger these events.
}

