using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;
using UnityEngine.EventSystems;
using BaroqueUI;
using System;

public class ControllersDialog : MonoBehaviour
{
    private void Gt_onControllersUpdate(Controller[] controllers)
    {
        bool triggerPressed = false;
        bool touchpadPressed = false;

        foreach (Controller ctrl in controllers)
        {
            if (ctrl.triggerPressed) triggerPressed = true;
            if (ctrl.touchpadPressed) touchpadPressed = true;
        }

        // This version uses safe short-circuit evaluation
        bool combinedButtonPressed = triggerPressed || touchpadPressed;

        // Display the combined button state to the user
        Text statusText = GetComponentInChildren<Text>();
        statusText.text = $"Trigger: {triggerPressed}, Touchpad: {touchpadPressed}, Combined: {combinedButtonPressed}";
    }
}

