[RequireComponent(typeof(ARRaycastManager))]
[RequireComponent(typeof(ARPlaneManager))]
public class AnchorCreator : MonoBehaviour
{

    [SerializeField]
    GameObject m_AnchorPrefab;

    public GameObject AnchorPrefab
    {
        get => m_AnchorPrefab;
        set => m_AnchorPrefab = value;
    }


    public void RemoveAllAnchors()
    {
        foreach (var anchor in m_AnchorPoints)
        {
            Destroy(anchor);
        }
        m_AnchorPoints.Clear();
    }





    void Awake()
    {
        m_RaycastManager = GetComponent<ARRaycastManager>();
        m_AnchorManager = GetComponent<ARAnchorManager>();
        m_PlaneManager = GetComponent<ARPlaneManager>();
        m_AnchorPoints = new List<ARAnchor>();
    }





    public void CreateAnchor()
    {
        var hit = new ARRaycastHit();

        if (m_RaycastManager.Raycast(new ARRaycastQuery { QueryType = QueryType.Plane, MaxHits = 1 }, out RaycastHit[] hits, trackableType: TrackableType.PlaneWithinPolygon))
        {
            hit = hits[0];
        }

        if (m_AnchorPoints.Count < 1)
        {
            var anchor = Instantiate(m_AnchorPrefab);
            anchor.name = "Anchor";
            anchor.transform.position = hit.pose.position;
            anchor.transform.rotation = hit.pose.rotation;

            m_AnchorPoints.Add(anchor);
        }
    }
}




