// This class is a coroutine that listens for the Hand's tracked controller to start tracking and then
// attempts to instantiate the item prefab, attaches it to the hand, and destroys the coroutine.
using UnityEngine;
using Valve.VR.InteractionSystem;

public class SpawnAndAttachAfterControllerIsTracking : MonoBehaviour
{
	public Hand hand;
	public GameObject itemPrefab;

	IEnumerator Start()
	{
		while (true)
		{
			if (itemPrefab!= null)
			{
				if (hand.controller!= null)
				{
					if (hand.controller.hasTracking)
					{
						// Instantiate the prefab
						GameObject objectToAttach = Instantiate(itemPrefab);
						objectToAttach.SetActive(true);

						// Attach it to the hand
						hand.AttachObject(objectToAttach);
						hand.controller.TriggerHapticPulse(800);

						// Destroy the coroutine
						Destroy(gameObject);
					}
				}
			}

			// Wait for the next frame
			yield return null;
		}
	}
}

This class uses a while loop that continually checks for the Hand's tracked controller to start tracking and then instantiates the prefab, attaches it to the hand, and destroys the coroutine. The yield return null statement at the end of the loop waits for the next frame before continuing.
