using UnityEngine;
using System.Collections;
using System.Collections.Generic;

public class waveViz : MonoBehaviour {
  Texture2D tex;
  public Renderer waverend;
  int wavewidth = 512;
  int waveheight = 64;
  public int period = 512;
  Color32[] wavepixels;
  int curWaveW = 0;
  int lastWaveH = 0;
  public Color32 waveBG = Color.black;
  public Color32 waveLine = Color.white;

  IEnumerator Start() {
    while(true) {
      yield return new WaitForSeconds(0.016f);
      //generate wave
      Wavetable(period, waveheight, wavewidth, ref lastWaveH, ref curWaveW);
      //update texture
      UpdateTexture();
    }
  }

  public void Wavetable(int p, int h, int w, ref int lh, ref int cw) {
    Color32[] pixels = new Color32[w * h];
    for (int i = 0; i < pixels.Length; i++) {
      //sin wave
      float amp = (float)Mathf.Sin((i / (float)w) * Mathf.PI * 2f);
      pixels[i] = new Color32(amp, amp, amp, 255);
    }
    //set texture
    tex = new Texture2D(w, h);
    tex.SetPixels32(pixels);
    tex.Apply();
    //assign to mesh
    waverend.material.SetTexture("_MainTex", tex);
    //update local vars
    lh = h;
    cw = w;
  }

  public void UpdateTexture() {
    //generate wave
    Wavetable(period, waveheight, wavewidth, ref lastWaveH, ref curWaveW);
    //update texture
    wavepixels = tex.GetPixels32();
  }
}


