﻿using UnityEngine;
using System.Collections;

namespace Valve.VR.InteractionSystem
{

	public class Balloon : MonoBehaviour
	{
		public enum BalloonColor { Red, OrangeRed, Orange, YellowOrange, Yellow, GreenYellow, Green, BlueGreen, Blue, VioletBlue, Violet, RedViolet, LightGray, DarkGray, Random };

		private Hand hand;

		public GameObject popPrefab;

		public float maxVelocity = 5f;

		public float lifetime = 15f;
		public bool burstOnLifetimeEnd = false;

		public GameObject lifetimeEndParticlePrefab;
		public SoundPlayOneshot lifetimeEndSound;

		private float destructTime = 0f;
		private float releaseTime = 99999f;

		public SoundPlayOneshot collisionSound;
		private float lastSoundTime = 0f;
		private float soundDelay = 0.2f;

		private Rigidbody balloonRigidbody;

		private bool bParticlesSpawned = false;

		private static float s_flLastDeathSound = 0f;



		void Start()
		{
			destructTime = Time.time + lifetime + Random.value;
			hand = GetComponentInParent<Hand>();
			balloonRigidbody = GetComponent<Rigidbody>();
		}



		void Update()
		{
			if ( ( destructTime!= 0 ) && ( Time.time > destructTime ) )
			{
				if ( burstOnLifetimeEnd )
				{
					SpawnParticles( lifetimeEndParticlePrefab, lifetimeEndSound );
				}
				Destroy(gameObject);
			}
		}



		void OnCollisionEnter( Collision collision )
		{
			if ( Time.time > lastSoundTime + soundDelay )
			{
				collisionSound.Play();
				lastSoundTime = Time.time;
			}

			// Reduce the lifetime of the balloon if it collides with something
			destructTime = Time.time + lifetime * 0.1f;

			// Check if the collision destroyed the balloon
			if ( destructTime < Time.time )
			{
				SpawnParticles( popPrefab, collisionSound );
			}
		}



		void OnEnable()
		{
			lastSoundTime = Time.time;
		}



		void OnDisable()
		{
			lastSoundTime = 0f;
		}



		void FixedUpdate()
		{
			if ( destructTime == 0 )
				return;

			// Throw the balloon if the hand is holding it
			if ( hand.heldObjects.Contains( this.gameObject ) )
			{
				Vector3 throwVelocity = ( transform.forward * maxVelocity * Input.GetAxis( "Vertical" ) + transform.right * maxVelocity * Input.GetAxis( "Horizontal" ) ).normalized;
				balloonRigidbody.AddForce( throwVelocity, ForceMode.VelocityChange );
			}
			// Otherwise, let the balloon float at the release height
			else
			{
				transform.position = new Vector3( transform.position.x, releaseTime, transform.position.z );
				balloonRigidbody.useGravity = false;
			}
		}



		void SpawnParticles( GameObject particlePrefab, SoundPlayOneshot particleSound )
		{
			// Don't spawn particles twice
			if ( bParticlesSpawned )
				return;

			GameObject particles = Instantiate( particlePrefab, transform.position, transform.rotation ) as GameObject;

			if ( particles )
			{
				ParticleSystem particlesSystem = particles.GetComponent<ParticleSystem>();
				if ( particlesSystem )
				{
					particlesSystem.Play();
				}
			}

			particleSound.Play();
			bParticlesSpawned = true;
		}
	}
}

