            enemyObject.transform.position = new Vector3(Random.Range(-4f, 4f), Random.Range(2.5f, 6.5f), player.cameraRig.transform.position.z + spawnDistanceFromPlayer);
        }

        gameTimer += Time.deltaTime;

        if (isGamerOver == false)
