using UnityEngine;

public class ArrowManager : MonoBehaviour
{
    public OVRInput.Controller controller;
    public GameObject arrowPrefab;
    public Transform arrowStartPoint;
    public Transform controler;
    public TextMesh debug;

    private GameObject currentArrow;
    private bool hasArrow = false;
    private bool isAttached = false;

    // Update is called once per frame
    void Update()
    {
        debug.text = controler.rotation.x.ToString() +
                     "\n" + controler.rotation.y.ToString() +
                     "\n" + controler.rotation.z.ToString();
        if (OVRInput.Get(OVRInput.Axis1D.SecondaryIndexTrigger) > 0)
        {
            if (!hasArrow)
            {
                AttachArrow();
            }
            else if (isAttached)
            {
                Fire();
            }
        }
        else ThrowArrow();
    }

    private void AttachArrow()
    {
        if (currentArrow == null)
        {
            currentArrow = Instantiate(arrowPrefab, controler);
            currentArrow.transform.localPosition = new Vector3(0.1f, 0f, 0.3f);
            currentArrow.GetComponent<Rigidbody>().isKinematic = true;
            hasArrow = true;
        }
    }

    private void Fire()
    {
        if (isAttached && OVRInput.Get(OVRInput.Axis1D.SecondaryIndexTrigger) < 0)
        {
            float dist = (arrowStartPoint.position - controler.position).magnitude;
            currentArrow.transform.parent = null;
            Rigidbody r = currentArrow.GetComponent<Rigidbody>();
            r.velocity = currentArrow.transform.forward * 25f * dist;
            r.useGravity = true;
            currentArrow.GetComponent<Collider>().isTrigger = false;
            currentArrow = null;
            hasArrow = false;
            isAttached = false;
        }
    }

    private void ThrowArrow()
    {
        if (currentArrow!= null)
        {
            currentArrow.transform.parent = null;
            currentArrow.transform.position = controler.position + controler.forward * 1.5f;
            currentArrow.GetComponent<Rigidbody>().isKinematic = false;
            currentArrow.GetComponent<Rigidbody>().AddForce(controler.forward * 50f, ForceMode.Impulse);
            currentArrow.GetComponent<Collider>().isTrigger = true;
            currentArrow = null;
            hasArrow = false;
        }
    }
}

