        a5 = GetPooledObject(gobj5);
        timer = 0;
        instantiate_gobj = true;
    }
    if (instantiate_gobj && timer >= timeLimit)
    {
        ReleaseInstance(a5);
        timer = 0;
        instantiate_gobj = false;
    }

    if (displayAlbumsOnNextFrame)
    {
        DisplayAlbumUI();
        displayAlbumsOnNextFrame = false;
    }
    else if (displayLibraryOnNextFrame)
    {
