        albumToDisplayOnNextFrame = null;
    }
}

// Object Pooling method
private GameObject GetPooledObject(GameObject obj)
{
    if (obj.activeSelf)
    {
        obj.SetActive(false);
        return obj;
    }
    else
    {
        return Instantiate(obj);
    }
}
