private IEnumerator InstantiateAndDestroyRoutine()
{
    while (true) // A loop that runs indefinitely
    {
        // Wait for the specified time limit
        yield return new WaitForSeconds(timeLimit);

        // Instantiate object and update flag
        a1 = Instantiate(gobj1);
        instantiate_gobj = true;

        // Wait again for the time limit
