				fireObject = (GameObject)Instantiate( fireParticlePrefab, transform.position, transform.rotation );
				if ( customParticles!= null )
				{
					customParticles = fireObject.GetComponent<ParticleSystem>();
					customParticles.Play();
				}
				isBurning = true;
