﻿using System.Collections;
using System.Collections.Generic;
using UnityEngine;

public class Enemy : MonoBehaviour
{
    public float enemyLifetime = 6f;



    void FixedUpdate()
    {
        enemyLifetime -= Time.fixedDeltaTime;
        if (enemyLifetime <= 0f) {
            Destroy(this.gameObject);
        }
    }
}
