using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.XR;

public class ARAnchoring : MonoBehaviour
{
    public Camera FirstPersonCamera;
    public GameObject Environment;
    public GameObject DetectedPlanePrefab;
    private List<DetectedPlane> m_AllPlanes = new List<DetectedPlane> ();
    public static bool isVR = false;

    public void Start ()
    {
        Screen.sleepTimeout = SleepTimeout.NeverSleep;
        FirstPersonCamera.GetComponent<ARCoreBackgroundRenderer> ().enabled = true;
    }

    void Update ()
    {
        if (isVR)
            return;

        Session.GetTrackables<DetectedPlane> (m_AllPlanes);
        for (int i = 0; i < m_AllPlanes.Count; i++)
        {
            // create game object for each plane found
            // use Instantiate() method for efficiency 
            var planeObject = Instantiate (DetectedPlanePrefab, transform);
            planeObject.GetComponent<DetectedPlaneVisualizer> ().Initialize (m_AllPlanes [i]);

            // use new Input.touch API 
            Touch touch;
            if (Input.touchCount < 1 || (touch = Input.GetTouch (0)).phase!= TouchPhase.Began)
                continue;

            // raycast from the center of the screen to the touch position 
            // this will return a HitResult that includes the plane that was hit 
            TrackableHit hit;
            TrackableHitFlags raycastFilter = TrackableHitFlags.PlaneWithinPolygon | TrackableHitFlags.FeaturePointWithSurfaceNormal;

            if (Frame.Raycast (Screen.width * 0.5f, Screen.height * 0.5f, raycastFilter, out hit))
            {
                // check if the hit was on a plane 
                if (hit.Trackable is DetectedPlane)
                {
                    // get the position of the camera 
                    Vector3 cameraPosition = FirstPersonCamera.transform.position;
                    // get the rotation of the camera 
                    Quaternion cameraRotation = FirstPersonCamera.transform.rotation;
                    // get the position of the hit point 
                    Vector3 hitPosition = hit.Pose.position;
                    // get the rotation of the hit point 
                    Quaternion hitRotation = hit.Pose.rotation;
                    // calculate the normal of the plane that was hit 
                    Vector3 planeNormal = hit.PlaneNormal;
                    // calculate the distance from the camera to the hit point 
                    float distanceToHitPoint = Vector3.Dot (cameraPosition - hitPosition, planeNormal);
                    // check if the camera is looking at the back of the plane 
                    if (distanceToHitPoint < 0)
                    {
                        // instantiate the environment game object at the hit point 
                        Instantiate (Environment, hitPosition, hitRotation);
                        // disable the ARCore background renderer 
                        FirstPersonCamera.GetComponent<ARCoreBackgroundRenderer> ().enabled = false;
                        // set the VR flag to true 
                        isVR = true;
                    }
                }
            }
        }
    }
}
