using UnityEngine;
using System.Collections;

namespace Valve.VR.InteractionSystem
{

    public class SpawnAndAttachAfterControllerIsTracking : MonoBehaviour
    {
        private Hand hand;
        public GameObject itemPrefab;

        void Start()
        {
            hand = GetComponentInParent<Hand>();
            hand.HandGrabBegin += OnHandGrabBegin;
        }

        private void OnHandGrabBegin(Hand hand)
        {
            if (!hand. IsValidatingPose)
            {
                return;
            }

            GameObject newObject = Instantiate(itemPrefab, hand.transform.position, hand.transform.rotation) as GameObject;
            newObject.transform.parent = hand.transform;
        }
    }

}

