            var thrownObject = Object.Instantiate(objectToThrow, attackPoint.position, attackPoint.rotation) as GameObject;
            var rb = thrownObject.GetComponent<Rigidbody>();
            rb.AddForce(cam.forward * throwForce, ForceMode.VelocityChange);
            throwCounter++;
        }
        else if (throwCounter == totalThrows)
