        }
        else if (bike.transform.position.z < cameraRig.transform.position.z - 2f)
        {
            cameraRig.transform.position = new Vector3(cameraRig.transform.position.x, cameraRig.transform.position.y, bike.transform.position.z + 1f);
        }
    }

}

