using System;
using UnityEngine.XR;

private static bool IsValidOpenVRDevice(uint deviceId)
{
    if (!XRSettings.enabled)
        return false;

    InputTracking.TrackingAcquired += (device) =>
    {
        if (device.index == deviceId)
            return;

        if (device.nodeType == XRNode.LeftHand || device.nodeType == XRNode.RightHand)
        {
            foreach (XRNode node in device.connectedNodes)
            {
                if (node == XRNode.Head &&!XRSettings.enableVirtualReality)
                    return;

                if (IsValidOpenVRDevice((uint)node))
                    return;
            }
        }
        else if (device.nodeType == XRNode.Head &&!XRSettings.enableVirtualReality)
        {
            foreach (XRNode node in device.connectedNodes)
            {
                if (IsValidOpenVRDevice((uint)node))
                    return;
            }
        }
    };

    return InputTracking.GetLocalPosition((int)deviceId)!= Vector3.zero;
}
